FUNCTION_BLOCK FB_SHUTTER_LEGACY
VAR_INPUT
	time_error_long_time_without_status : TIME; // Таймер проверки долгого ожидания сигнала
	ixready : BOOL; // Затвор готовность
	ixerror : BOOL; //Затвор ошибка (вход)
	ixopen : BOOL; // Сигнал на открытие затвора
	ixclose : BOOL; // Сигнал на закрытие затвора
	ixreset : BOOL; // Действие пользователя при возниковении ошибки / аварии
	ixopened_status : BOOL; // Статус от устройства на открытие затвора
	ixclosed_status : BOOL; // Статус от устройства на закрытие затвора
END_VAR
VAR_OUTPUT
	qxopen : BOOL; // Открыть затвор
	qxclose : BOOL; // Закрыть затвор
	qxerror_from_device : BOOL; // Затвор ошибка
	qxerror_long_time_without_status : BOOL; // Ошибка долгого ожидания ixready
	error_long_time_without_status_open_closed : BOOL; // Ошибка долгого ожидания статуса открыт/закрыт
	qxopened : BOOL; // Затвор открыт
	qxclosed : BOOL; // Затвор закрыт
	qxclose_process : BOOL; // Закрытие затвора в процессе
	qxopen_process : BOOL; // Закрытие затвора в процессе
END_VAR
VAR
	error_fixation : BOOL; // Фиксация положения при возникновении ошибки
	rtrig_open : R_TRIG;
	rtrig_close : R_TRIG;
	rtrig_ixopened_status : R_TRIG;
	rtrig_ixclosed_status : R_TRIG;
	rtrig_ixreset : R_TRIG;
	rtrig_ixerror : R_TRIG;
	ftrig_ixopened_status : F_TRIG;
	ftrig_ixclosed_status : F_TRIG;

	// Таймеры на открытие/закрытие
	delay_qxopen : TON;
	delay_qxclose : TON;
	delay_time : TIME := T#2S;
	// Обработка ошибок долгого ожидания
	ton_error_long_time_without_status : TON;
	ton_error_long_time_without_status_open_closed : TON;
