FUNCTION_BLOCK FB_SHUTTER_LEGACY
VAR_INPUT
	time_error_long_time_without_status : TIME; // Таймер проверки долгого ожидания сигнала
	ixready : BOOL; // Затвор готовность
	ixerror : BOOL; //Затвор ошибка (вход)
	ixopen : BOOL; // Сигнал на открытие затвора
	ixclose : BOOL; // Сигнал на закрытие затвора
	ixreset : BOOL; // Действие пользователя при возниковении ошибки / аварии
	ixopened_status : BOOL; // Статус от устройства на открытие затвора
	ixclosed_status : BOOL; // Статус от устройства на закрытие затвора
END_VAR
VAR_OUTPUT
	qxopen : BOOL; // Открыть затвор
	qxclose : BOOL; // Закрыть затвор
	qxerror_from_device : BOOL; // Затвор ошибка
	qxerror_long_time_without_status : BOOL; // Ошибка долгого ожидания ixready
	error_long_time_without_status_open_closed : BOOL; // Ошибка долгого ожидания статуса открыт/закрыт
	qxopened : BOOL; // Затвор открыт
	qxclosed : BOOL; // Затвор закрыт
	qxclose_process : BOOL; // Закрытие затвора в процессе
	qxopen_process : BOOL; // Закрытие затвора в процессе
END_VAR
VAR
	error_fixation : BOOL; // Фиксация положения при возникновении ошибки
	rtrig_open : R_TRIG;
	rtrig_close : R_TRIG;
	rtrig_ixopened_status : R_TRIG;
	rtrig_ixclosed_status : R_TRIG;
	rtrig_ixreset : R_TRIG;
	rtrig_ixerror : R_TRIG;
	ftrig_ixopened_status : F_TRIG;
	ftrig_ixclosed_status : F_TRIG;

	// Таймеры на открытие/закрытие
	delay_qxopen : TON;
	delay_qxclose : TON;
	delay_time : TIME := T#2S;
	// Обработка ошибок долгого ожидания
	ton_error_long_time_without_status : TON;
	ton_error_long_time_without_status_open_closed : TON;
END_VAR

ST.xhtml:
// 	===	Обработка ошибок ===


// Сброс ошибок пользователем
rtrig_ixreset(CLK:= ixreset, Q=> );
IF rtrig_ixreset.Q THEN 
	error_fixation := FALSE; 
	qxerror_from_device := FALSE;
END_IF;

// Фиксация статуса ошибки
rtrig_ixerror(CLK:= ixerror, Q=> );
IF 	rtrig_ixerror.Q
	OR qxerror_long_time_without_status
	THEN
		error_fixation := TRUE;
//		qxerror_long_time_without_status := FALSE;
END_IF;
IF rtrig_ixerror.Q THEN qxerror_from_device := TRUE; END_IF;

// Обработка ошибки длительного ожидания статуса открыт/закрыт
ton_error_long_time_without_status_open_closed(
	IN:= qxclose_process OR qxopen_process, 
	PT:= time_error_long_time_without_status, 
	Q=> error_long_time_without_status_open_closed, 
	ET=> );


// 	===	Работа открытия/закрытия ===


rtrig_open(CLK:= ixopen, Q=> );
rtrig_close(CLK:= ixclose, Q=> );

rtrig_ixopened_status(CLK:= ixopened_status, Q=> );
rtrig_ixclosed_status(CLK:= ixclosed_status, Q=> );

ftrig_ixopened_status(CLK:= ixopened_status, Q=> );
ftrig_ixclosed_status(CLK:= ixclosed_status, Q=> );

delay_qxopen(IN:= qxopen, PT:= delay_time, Q=> , ET=> );
delay_qxclose(IN:= qxclose, PT:= delay_time, Q=> , ET=> );

IF rtrig_ixopened_status.Q THEN 
	qxopened := TRUE; 
	qxopen_process := FALSE;
	qxopen := FALSE;
END_IF;
IF rtrig_ixclosed_status.Q THEN 
	qxclosed := TRUE; 
	qxclose_process := FALSE;
	qxclose := FALSE;
END_IF;

IF ftrig_ixopened_status.Q THEN qxopened := FALSE; END_IF;
IF ftrig_ixclosed_status.Q THEN qxclosed := FALSE; END_IF;

IF 	rtrig_open.Q 
	AND ixready
	AND NOT qxclose 
	AND NOT error_fixation
	AND NOT qxopen_process
	THEN
		qxopen := TRUE;
		qxopen_process:= TRUE;
END_IF;

// IF delay_qxopen.Q THEN qxopen := FALSE; END_IF;


IF 	rtrig_close.Q 
	AND ixready
	AND NOT qxopen
	AND NOT error_fixation
	AND NOT qxclose_process
	THEN
		qxclose := TRUE;
		qxclose_process := TRUE;
END_IF;

// IF delay_qxclose.Q THEN qxclose := FALSE; END_IF;

