tof_FSignalDelay( IN:= ton_SignalDelay.Q, PT:= itInputTime, Q=> qxSignal, ET=> );
rtrig_ixSignal(CLK:= ixSignal, Q=> ); rtrig_qxRattlingAlarm(CLK:= ctu_rattling.Q, Q=> );
IF rtrig_qxRattlingAlarm.Q THEN qxRattlingAlarm := TRUE; END_IF;
ctu_rattling( CU:= rtrig_ixSignal.Q, RESET:= ton_rattling_count_reset.Q, PV:= RATTLING_FUN(itTime:= itRattling_count_reset), Q=> , CV=> );
ton_rattling_count_reset( IN:= NOT ton_rattling_count_reset.Q, PT:= itRattling_count_reset, Q=> , ET=> );

