FUNCTION_BLOCK FB_FRQ_CTRL
VAR_INPUT
	irMaxFreq : REAL; // Максимальное значение частоты
	ixStart : BOOL; // Запуск
	ixChange : BOOL; // Переключение с байпас на ЧРП (старт контактора без плавного пуска)
	ixStop : BOOL; // Остановка
	ixEmergencyStop : BOOL; // Аварийная остановка
	itUpdateCycle : TIME; // Время обновления цикла смены частоты
	itCmdDelayTime : TIME; // Время задержки выключения выходных сигналов
	irUpdateCycleStep : REAL; // Шаг изменения частоты за 1 цикл itUpdateCycle
	irActualFreq : REAL; // Показания текущей частоты устройства
	irActualFreqInaccuracy : REAL; // Погрешность показаний частоты устройства
	ixDeviceReady : BOOL; // Готовность исполнительного механизма
	ixDeviceReserve : BOOL; // True - статус механизма - РЕЗЕРВНЫй
	ixDeviceReserveOn: BOOL; // True - при условии статуса РЕЗЕРВ, переключение работы на текущий механизм
	ixDeviceError : BOOL; // Ошибка исполнительного механизма
	ixReset : BOOL; // Сброс ошибок и аварийной остановки
	ixSmoothStop : BOOL; // Режим плавной остановки\
