VAR_INPUT
	actualFreq : REAL;
	actualFreqInaccuracy : REAL;
	orderFreq : REAL;
END_VAR
VAR
END_VAR

ST.xhtml:
IF actualFreq < orderFreq THEN
	FC_PossibleInac := orderFreq - actualFreq <= actualFreqInaccuracy;
ELSIF actualFreq > orderFreq THEN
	FC_PossibleInac :=actualFreq - orderFreq <= actualFreqInaccuracy;
ELSIF actualFreq = orderFreq THEN
	FC_PossibleInac := TRUE;
ELSE
	RETURN;
END_IF;

