END_VAR

ST.xhtml:
blink_signal(
	ENABLE:= TRUE, 
	TIMELOW:= itDelay, 
	TIMEHIGH:= T#100MS, 
	OUT=> );

rtrig_signal(CLK:= blink_signal.OUT, Q=> );

IF rtrig_signal.Q AND ixReverseInac THEN
	qrActualFreq := irOrderFreq - irInac;

ELSE
	qrActualFreq := irOrderFreq + irInac;
END_IF;

