FUNCTION_BLOCK FB_A_SIGNAL_SCALE VAR_INPUT irSignal : REAL; // Вход аналогового сигнала irSignalSim : REAL; // Вход аналогового сигнала (симулятор) irSignalDownLimit : REAL; // Нижний предел сигнала irSignalUpLimit : REAL; // Верхний предел сигнала irValueDownLimit : REAL; // Нижний предел масштабированной единицы irValueUpLimit : REAL; // Верхий предел масштабированной единицы irHSignal : REAL; // Верхний предупредительный сигнал irHHSignal : REAL; // Верхний аварийный сигнал irLSignal : REAL; // Нижний предупредительный сигнал irLLSignall : REAL; // Верхний предупредительный сигнал ixReset : BOOL; // Ресет ошибок ixDeviceOn : BOOL; // Датчик работает ixSimulation : BOOL; // Режим симуляции сигнала END_VAR VAR_OUTPUT qrSignal : REAL; // Выходной масштабированный сигнал qxMaxSignal : BOOL; // Достижение минимального сигнала qxMinSignal : BOOL; // Достижение максимального сигнала qxErrorMin : BOOL; // Ошибка: сигнал меньше, чем минимально допустимый qxErrorMax : BOOL; // Ошибка: сигнал больше, чем максимально допустимый qxHSignal : BOOL; // Достижение верхнего предупредительного сигнала qxHHSignal : BOOL; // Достижение верхнего аварийного сигнала qxLSignal : BOOL; // Достижение нижнего предупредительного сигнала qxLLSignal : BOOL; // Достижение нихнего аварийного сигнала END_VAR VAR rtrig_qxMaxSignal : R_TRIG; rtrig_qxMinSignal : R_TRIG;
