ST.xhtml:
rtrig_ixReset(CLK:= ixReset, Q=> );

IF ixDeviceOn THEN
	rtrig_qxMaxSignal(CLK:= irSignal = irSignalUpLimit, Q=> );
	rtrig_qxMinSignal(CLK:= irSignal = irSignalDownLimit, Q=> );

	rtrig_qxErrorMax(CLK:= irSignal > irSignalUpLimit, Q=> );
	rtrig_qxErrorMin(CLK:= irSignal < irSignalDownLimit, Q=> );

	rtrig_qxHSignal(CLK:= irHSignal < qrSignal AND NOT qxHSignal, Q=> );
	rtrig_qxHHSignal(CLK:= irHHSignal < qrSignal AND NOT qxHHSignal, Q=> );
	rtrig_qxLSignal(CLK:= irLSignal > qrSignal AND NOT qxLSignal, Q=> );
	rtrig_qxLLSignal(CLK:= irLLSignall > qrSignal AND NOT qxLLSignal, Q=> );

	IF NOT qxErrorMin AND NOT qxErrorMax AND NOT ixSimulation THEN 
		qrSignal := ((irSignal - irSignalDownLimit) * (irValueUpLimit - irValueDownLimit)) / (irSignalUpLimit - irSignalDownLimit) + irValueDownLimit;
	END_IF;

ELSIF NOT qxErrorMin AND NOT qxErrorMax AND ixSimulation THEN
	qrSignal := ((irSignalSim - irSignalDownLimit) * (irValueUpLimit - irValueDownLimit)) / (irSignalUpLimit - irSignalDownLimit) + irValueDownLimit;
ELSE
	qrSignal := 0;
END_IF;

IF rtrig_qxHSignal.Q THEN qxHSignal := TRUE; END_IF;
IF rtrig_qxHHSignal.Q THEN qxHHSignal := TRUE; END_IF;
IF rtrig_qxLSignal.Q THEN qxLSignal := TRUE; END_IF;
IF rtrig_qxLLSignal.Q THEN qxLLSignal := TRUE; END_IF;

IF rtrig_ixReset.Q THEN
	qxHSignal := FALSE;
	qxHHSignal := FALSE;
	qxLSignal := FALSE;
	qxLLSignal := FALSE;
END_IF;

IF rtrig_qxMaxSignal.Q THEN qxMaxSignal := TRUE; END_IF;
IF rtrig_qxMinSignal.Q THEN qxMinSignal := TRUE; END_IF;
IF rtrig_qxErrorMax.Q THEN qxErrorMax := TRUE; END_IF;
IF rtrig_qxErrorMin.Q THEN qxErrorMin := TRUE; END_IF;

IF rtrig_ixReset.Q THEN
		qxMaxSignal := FALSE;
		qxMinSignal := FALSE;
		qxErrorMax := FALSE;
		qxErrorMin := FALSE;
END_IF;

