ST.xhtml:
qxWorkProcess := ixWorkProcess;
qxError := ixError;
xStartCondition := rtrig_ixStart.Q AND NOT ixError AND NOT qxStartStatus;
xStopCondition := rtrig_ixStop.Q AND NOT ixError AND NOT qxStopStatus;

// Работа на старт

rtrig_ixStart(CLK:= ixStart, Q=> );
ftrig_qxStartStatus(CLK:= tof_StartProcess.Q, Q=> );

tof_StartProcess(
	IN:= rtrig_ixStart.Q AND NOT ixError,
	PT:= itStartTime, 
	Q=> qxStartProcess, 
	ET=> );

IF xStartCondition THEN qxSignal := TRUE; qxStopProcess := FALSE; qxStopStatus := FALSE; END_IF;
IF ftrig_qxStartStatus.Q THEN qxStartStatus := TRUE; END_IF;

// Работа на стоп

rtrig_ixStop(CLK:= ixStop, Q=> );
ftrig_qxStopStatus(CLK:= tof_StopProcess.Q, Q=> );

tof_StopProcess(
	IN:= rtrig_ixStop.Q AND NOT ixError, 
	PT:= itStopTime, 
	Q=> qxStopProcess, 
	ET=> );

IF xStopCondition THEN qxSignal := FALSE; qxStartProcess := FALSE; qxStartStatus := FALSE; END_IF;
IF ftrig_qxStopStatus.Q THEN qxStopStatus := TRUE; END_IF;

