	ixDeviceReserve : BOOL; // True - статус механизма - РЕЗЕРВНЫй
	ixDeviceReserveOn: BOOL; // True - при условии статуса РЕЗЕРВ, переключение работы на текущий механизм
	ixDeviceError : BOOL; // Ошибка исполнительного механизма
	ixReset : BOOL; // Сброс ошибок и аварийной остановки
	ixSmoothStop : BOOL; // Режим плавной остановки\
	ixStarted : BOOL; // Получение сигнала о старте механизма
	ixStopped : BOOL; // Получение сигнала о стопе механизма
END_VAR
