ST.xhtml:
// Инициализация триггеров
rtrig_ixStart(CLK:= ixStart);
rtrig_ixChange(CLK:= ixChange);
rtrig_ixStop(CLK:= ixStop);
rtrig_ixEmergencyStop(CLK:= ixEmergencyStop);
ftrig_stopPulse(CLK:= qrFreq > 0);
rtrig_qxDeviceError(CLK:= ixDeviceError);
rtrig_ixReset(CLK:= ixReset);
rtrig_updateCycle(CLK:= blink_updateCycle.OUT);

// Основная логика
ProcessReset();
ProcessStartStopCommands();
ProcessFrequencyControl();
ProcessErrorHandling();
ProcessTimers();

// Сброс статусов при завершении
IF ton_stopWorkProcess.Q AND (NOT qxStopStatus OR NOT qxStartStatus) THEN 
    xWorkProcess := FALSE; 
    qxStopStatus := FALSE; 
    qxStartStatus := FALSE;
    RETURN;
END_IF;

// Обработка команд
CASE TRUE OF
    rtrig_ixStart.Q: 
        IF CheckStartConditions() THEN 
            xWorkProcess := TRUE; 
            qxStartStatus := TRUE;
        END_IF;

    rtrig_ixChange.Q: 
        IF CheckStartConditions() THEN 
            xWorkProcess := TRUE; 
            qxStartStatus := TRUE;
        END_IF;

    rtrig_ixStop.Q: 
        IF NOT xErrorFixation AND NOT qxEmergencyStopStatus THEN 
            reserve_iqrSetFreq := iqrSetFreq;
            iqrSetFreq := 0; 
            qxStopStatus := TRUE;
        END_IF;

    rtrig_ixEmergencyStop.Q: 
        xWorkProcess := FALSE;
        qxEmergencyStopStatus := TRUE;
END_CASE;

IF rtrig_ixStop.Q AND NOT xErrorFixation AND NOT qxEmergencyStopStatus THEN
    IF NOT ixSmoothStop THEN 
        qrFreq := 0; 
    END_IF;
ELSIF rtrig_ixEmergencyStop.Q THEN 
    qrFreq := 0;
ELSIF ftrig_stopPulse.Q THEN 
    iqrSetFreq := reserve_iqrSetFreq; 
    xWorkProcess := FALSE; 
END_IF;

// Прямая установка частоты для CHANGE
IF rtrig_ixChange.Q AND CheckStartConditions() THEN
    qrFreq := iqrSetFreq;
END_IF;

// Плавное изменение частоты
IF ShouldUpdateFrequency() THEN
    qrFreq := CalculateNewFrequency();
END_IF;

// Обработка остановки и аварийных ситуаций
ProcessStopActions();

// Обработка команды STOP
IF ftrig_stopPulse.Q OR rtrig_ixEmergencyStop.Q THEN
	qxStop := TRUE;
ELSIF ixStopped THEN
	qxStop := FALSE;
ELSIF ton_qxStopDisable.Q THEN
	qxStop := FALSE;
	qxDelayError := TRUE;
END_IF;

// Обработка команды START
IF (rtrig_ixStart.Q OR rtrig_ixChange.Q) AND NOT qxEmergencyStopStatus AND NOT xErrorFixation AND ixDeviceReady THEN
	qxStart := TRUE;
ELSIF ixStarted THEN
	qxStart := FALSE;
ELSIF ton_qxStartDisable.Q THEN
	qxStart := FALSE;
	qxDelayError := TRUE;
END_IF;

// Обновление статусов работы
UpdateWorkStatus();

IF rtrig_ixReset.Q THEN
    xErrorFixation := FALSE;
    qxEmergencyStopStatus := FALSE;
    qxErrorInvalidInaccuracy := FALSE;
    qxDeviceError := FALSE;
	qxDelayError := FALSE;
END_IF;

IF iqrSetFreq + irUpdateCycleStep < qrFreq THEN
	CalculateNewFrequency := qrFreq - irUpdateCycleStep;
ELSIF iqrSetFreq - irUpdateCycleStep > qrFreq THEN
	CalculateNewFrequency := qrFreq + irUpdateCycleStep;
ELSE
	CalculateNewFrequency := iqrSetFreq;
END_IF;

IF irActualFreq < qrFreq THEN
	CheckFrequencyInaccuracy := qrFreq - irActualFreq <= irActualFreqInaccuracy;
ELSIF irActualFreq > qrFreq THEN
	CheckFrequencyInaccuracy :=irActualFreq - qrFreq <= irActualFreqInaccuracy;
ELSIF irActualFreq = qrFreq THEN
	CheckFrequencyInaccuracy := TRUE;
ELSE
	CheckFrequencyInaccuracy := FALSE;
END_IF;

// Проверка погрешности частоты
qxErrorInvalidInaccuracy := NOT CheckFrequencyInaccuracy();

// Фиксация ошибок устройства
IF rtrig_qxDeviceError.Q THEN
    qxDeviceError := TRUE;
END_IF;

// Общая фиксация ошибок
xErrorFixation := qxErrorInvalidInaccuracy OR qxDeviceError;

// Таймеры для команд
ton_qxStopDisable(IN:= qxStop, PT:= itCmdDelayTime);
ton_qxStartDisable(IN:= qxStart, PT:= itCmdDelayTime);

// Таймер для цикла обновления
blink_updateCycle(
    ENABLE:= xWorkProcess, 
    TIMELOW:= itUpdateCycle, 
    TIMEHIGH:= T#100MS
);

// Таймер остановки рабочего процесса
ton_stopWorkProcess(
    IN:= xWorkProcess AND qrFreq = 0, 
    PT:= T#5S
);

InterfaceAsPlainText.xhtml:
FUNCTION_BLOCK FB_TEST_FRQ_CTRL
VAR_INPUT
	irMaxFreq : REAL; // Максимальное значение частоты
	ixStart : BOOL; // Запуск
	ixChange : BOOL; // Переключение с байпас на ЧРП (старт контактора без плавного пуска)
	ixStop : BOOL; // Остановка
	ixEmergencyStop : BOOL; // Аварийная остановка
	itUpdateCycle : TIME; // Время обновления цикла смены частоты
	itCmdDelayTime : TIME; // Время задержки выключения выходных сигналов
	irUpdateCycleStep : REAL; // Шаг изменения частоты за 1 цикл itUpdateCycle
	irActualFreq : REAL; // Показания текущей частоты устройства
	irActualFreqInaccuracy : REAL; // Погрешность показаний частоты устройства
	ixDeviceReady : BOOL; // Готовность исполнительного механизма
	ixDeviceReserve : BOOL; // True - статус механизма - РЕЗЕРВНЫй
	ixDeviceReserveOn: BOOL; // True - при условии статуса РЕЗЕРВ, переключение работы на текущий механизм
	ixDeviceError : BOOL; // Ошибка исполнительного механизма
	ixReset : BOOL; // Сброс ошибок и аварийной остановки
	ixSmoothStop : BOOL; // Режим плавной остановки\
	ixStarted : BOOL; // Получение сигнала о старте механизма
	ixStopped : BOOL; // Получение сигнала о стопе механизма
END_VAR
VAR_OUTPUT
	qrFreq : REAL; // Задание частоты на выход
	qxEmergencyStopStatus : BOOL; // Статус аварийной остановки
	qxStart : BOOL; // Запуск исполнительного механизма
	qxStartStatus : BOOL; // Статус запущен
	qxStop : BOOL; // Остановка исполнительного механизма
	qxStopStatus : BOOL; // Статус остановлен
	qxErrorInvalidInaccuracy : BOOL; // Ошибка большой погрешности
	qxDeviceError : BOOL; // Ошибка исполнительного механизма (выход через блок)
	qxDelayError : BOOL; // Ошибка долгого ожидания обратной связи механизма после выполнения команды
END_VAR
VAR_IN_OUT
	iqrSetFreq : REAL; // Задание частоты на вход
END_VAR
VAR
    // Триггеры и таймеры
    rtrig_ixStart : R_TRIG;
    rtrig_ixChange : R_TRIG;
    rtrig_ixStop : R_TRIG;
    rtrig_ixEmergencyStop : R_TRIG;
    rtrig_updateCycle : R_TRIG;
    rtrig_qxDeviceError : R_TRIG;
    rtrig_ixReset : R_TRIG;
    ftrig_stopPulse : F_TRIG;
    blink_updateCycle : BLINK;
    ton_stopWorkProcess : TON;
    ton_qxStopDisable : TON;
    ton_qxStartDisable : TON;

    // Внутренние состояния
    xWorkProcess : BOOL;
    xErrorFixation : BOOL;
    reserve_iqrSetFreq : REAL;
END_VAR

