Auto_iqrSetFreq:= autoCmd.Auto_iqrSetFreq, Service_iqrSetFreq:= ,
Auto_ixKM_PU01:= autoCmd.Auto_ixKM_PU01, Auto_ixKM_PU02:= autoCmd.Auto_ixKM_PU02, Auto_ixKM_PU03:= autoCmd.Auto_ixKM_PU03, Auto_ixKM_PU04:= autoCmd.Auto_ixKM_PU04, Auto_ixKM_PU05:= autoCmd.Auto_ixKM_PU05,
Service_ixKM_PU01:= , Service_ixKM_PU02:= , Service_ixKM_PU03:= , Service_ixKM_PU04:= , Service_ixKM_PU05:= ,
qxFrqStart=> varVFD001.ixStart, qxFrqChange=> varVFD001.ixChange, qxFrqStop=> varVFD001.ixStop, qxFrqEmergencyStop=> varVFD001.ixEmergencyStop, qxFrqReset=> varVFD001.ixReset, qxFrqSmoothStop=> varVFD001.ixSmoothStop, qrSetFreq=> VFD001_iqrSetFreq, qxKM_PU01=> ixKM_PU01, qxKM_PU02=> ixKM_PU02, qxKM_PU03=> ixKM_PU03, qxKM_PU04=> ixKM_PU04, qxKM_PU05=> ixKM_PU05);
// Статус работы контакторов ЧРП и байпас и условия обратного перехода CASE iIPU_Select OF 1: xKMFrqReady := NOT _0303_05_PU01_ixKM_StatusOn_Rattling.qxSignal AND NOT _0303_05_PU01_ixKM_Frq_StatusOn_Rattling.qxSignal; IF VFD001_rtrig_ixStart.Q OR VFD001_rtrig_ixChange.Q THEN _0303_05_PU01_qxKM_cmdOn := FALSE; END_IF; varVFD001.ixStarted := _0303_05_PU01_ixKM_Frq_StatusOn_Rattling.qxSignal; 2: xKMFrqReady := NOT _0303_05_PU02_ixKM_StatusOn_Rattling.qxSignal AND NOT _0303_05_PU02_ixKM_Frq_StatusOn_Rattling.qxSignal; IF VFD001_rtrig_ixStart.Q OR VFD001_rtrig_ixChange.Q THEN _0303_05_PU02_qxKM_cmdOn := FALSE; END_IF; varVFD001.ixStarted := _0303_05_PU02_ixKM_Frq_StatusOn_Rattling.qxSignal; 3: xKMFrqReady := NOT _0303_05_PU03_ixKM_StatusOn_Rattling.qxSignal AND NOT _0303_05_PU03_ixKM_Frq_StatusOn_Rattling.qxSignal; IF VFD001_rtrig_ixStart.Q OR VFD001_rtrig_ixChange.Q THEN _0303_05_PU03_qxKM_cmdOn := FALSE; END_IF; varVFD001.ixStarted := _0303_05_PU03_ixKM_Frq_StatusOn_Rattling.qxSignal; 4: xKMFrqReady := NOT _0303_05_PU04_ixKM_StatusOn_Rattling.qxSignal AND NOT _0303_05_PU04_ixKM_Frq_StatusOn_Rattling.qxSignal; IF VFD001_rtrig_ixStart.Q OR VFD001_rtrig_ixChange.Q THEN _0303_05_PU04_qxKM_cmdOn := FALSE; END_IF; varVFD001.ixStarted := _0303_05_PU04_ixKM_Frq_StatusOn_Rattling.qxSignal; 5: xKMFrqReady := NOT _0303_05_PU05_ixKM_StatusOn_Rattling.qxSignal AND NOT _0303_05_PU05_ixKM_Frq_StatusOn_Rattling.qxSignal; IF VFD001_rtrig_ixStart.Q OR VFD001_rtrig_ixChange.Q THEN _0303_05_PU05_qxKM_cmdOn := FALSE; END_IF; varVFD001.ixStarted := _0303_05_PU05_ixKM_Frq_StatusOn_Rattling.qxSignal; END_CASE;
VFD001( // ЧРП ОСНОВНОЙ irMaxFreq:= varVFD001.irMaxFreq, // Максимально допустимая частота ixStart:= VFD001_rtrig_ixStart.Q AND xKMFrqReady, // Старт от rtrig при условии, что мы не получаем сигнал от контакторов байпас ixChange:= VFD001_rtrig_ixChange.Q, // Переключение с байпас на ЧРП (старт без плавного пуска) ixStop:= VFD001_rtrig_ixStop.Q, // Стоп через rtrig ixEmergencyStop:= varVFD001.ixEmergencyStop, // Аварийная остановка и отправление сигнала обратной связи ФБ itUpdateCycle:= varVFD001.itUpdateCycle, // Время обновления цикла плавного пуска/остановки в TIME irUpdateCycleStep:= varVFD001.irUpdateCycleStep, // Шаг обновления частоты за 1 цикл irActualFreq:= VFD001_DebugActualFreqSim.qrActualFreq, // _0303_05_VFD001_MainFrq_ActualFrequency.Value - для прибора ; VFD001_DebugActualFreqSim.qrActualFreq - для симуляции || Получение сигнала частоты от прибора в REAL irActualFreqInaccuracy:= varVFD001.irActualFreqInaccuracy, // Максимальная допустимая погрешность для ЧРП ixDeviceReady:= _0303_05_1_ixFreq_Ready_Rattling.qxSignal AND NOT _0303_05_1_ixFreq_Manual_Rattling.qxSignal, // Получение сигнала о готовности от ЧРП ixDeviceError:= _0303_05_1_ixFreq_Failure_Rattling.qxSignal, // Получение сигнала об ошибке от ЧРП ixDeviceReserve:= FALSE, // Статус ЧРП резерв ixDeviceReserveOn:= _0303_05_1_ixFreq_Failure_Rattling.qxSignal, // Переключение работы ЧРП на резерв ixReset:= varVFD001.ixReset, // Сброс всех ошибок ixSmoothStop := varVFD001.ixSmoothStop, // Опция плавной остановки. TRUE - остановка происходит плавным переходом частоты от текущей до нуля, согласно шагу цикла. FALSE - остановка происходит сразу, выходная частота ФБ сбрасывается до 0. ixStarted := varVFD001.ixStarted, ixStopped := VFD001_ixStopped.Q, qrFreq=> , // SetFreqAnaloOutputSignalScale qxEmergencyStopStatus=> varVFD001.qxEmergencyStopStatus, // обратная связь ФБ qxStart=> , // по выбору насоса qxStartStatus=> varVFD001.qxStartStatus, // обратная связь ФБ qxStop=> , // по выбору насоса qxStopStatus=> varVFD001.qxStopStatus, // обратная связь ФБ qxErrorInvalidInaccuracy=> varVFD001.qxErrorInvalidInaccuracy, // обратная связь ФБ qxDeviceError=> varVFD001.qxDeviceError, // обратная связь ФБ qxDelayError=> varVFD001.qxDelayError, iqrSetFreq:= VFD001_iqrSetFreq); // Задание частоты. При остановке сбрасывает значение до 0, после процедуры остановки возвращает прошлое значение.
VFD002( // ЧРП РЕЗЕРВ irMaxFreq:= varVFD001.irMaxFreq, // Максимально допустимая частота ixStart:= VFD001_rtrig_ixStart.Q AND xKMFrqReady, // Старт от rtrig при условии, что мы не получаем сигнал от контакторов байпас ixChange:= VFD001_rtrig_ixChange.Q, // Переключение с байпас на ЧРП (старт без плавного пуска) ixStop:= VFD001_rtrig_ixStop.Q, // Стоп через rtrig ixEmergencyStop:= varVFD002.ixEmergencyStop, // Аварийная остановка и отправление сигнала обратной связи ФБ itUpdateCycle:= varVFD001.itUpdateCycle, // Время обновления цикла плавного пуска/остановки в TIME irUpdateCycleStep:= varVFD001.irUpdateCycleStep, // Шаг обновления частоты за 1 цикл irActualFreq:= VFD002_DebugActualFreqSim.qrActualFreq, // _0303_05_VFD001_MainFrq_ActualFrequency.Value - для прибора ; VFD001_DebugActualFreqSim.qrActualFreq - для симуляции || Получение сигнала частоты от прибора в REAL irActualFreqInaccuracy:= varVFD001.irActualFreqInaccuracy, // Максимальная допустимая погрешность для ЧРП ixDeviceReady:= _0303_05_2_ixFreq_Ready_Rattling.qxSignal AND NOT _0303_05_2_ixFreq_Manual_Rattling.qxSignal, // Получение сигнала о готовности от ЧРП ixDeviceError:= _0303_05_2_ixFreq_Failure_Rattling.qxSignal, // Получение сигнала об ошибке от ЧРП ixDeviceReserve:= TRUE, // Статус ЧРП резерв ixDeviceReserveOn:= _0303_05_1_ixFreq_Failure_Rattling.qxSignal, // Переключение работы ЧРП на резерв ixReset:= varVFD002.ixReset, // Сброс всех ошибок ixSmoothStop := varVFD002.ixSmoothStop, // Опция плавной остановки. TRUE - остановка происходит плавным переходом частоты от текущей до нуля, согласно шагу цикла. FALSE - остановка происходит сразу, выходная частота ФБ сбрасывается до 0. ixStarted := varVFD001.ixStarted, ixStopped := VFD001_ixStopped.Q, qrFreq=> , // SetFreqAnaloOutputSignalScale qxEmergencyStopStatus=> varVFD002.qxEmergencyStopStatus, // обратная связь ФБ qxStart=> , // по выбору насоса qxStartStatus=> varVFD002.qxStartStatus, // обратная связь ФБ qxStop=> , // по выбору насоса qxStopStatus=> varVFD002.qxStopStatus, // обратная связь ФБ qxErrorInvalidInaccuracy=> varVFD002.qxErrorInvalidInaccuracy, // обратная связь ФБ qxDeviceError=> varVFD002.qxDeviceError, // обратная связь ФБ qxDelayError=> , iqrSetFreq:= VFD001_iqrSetFreq); // Задание частоты. При остановке сбрасывает значение до 0, после процедуры остановки возвращает прошлое значение.
VFD001_rtrig_ixStop(CLK:= varVFD001.ixStop, Q=> ); // Стоп работы VFD001 VFD001_rtrig_ixChange(CLK:= varVFD001.ixChange, Q=> ); // Старт работы VFD001 (без плавного пуска) VFD001_rtrig_ixStart(CLK:= varVFD001.ixStart, Q=> ); // Старт работы VFD001
xPuKmFrqWorked := _0303_05_PU01_ixKM_Frq_StatusOn_Rattling.qxSignal OR _0303_05_PU02_ixKM_Frq_StatusOn_Rattling.qxSignal OR _0303_05_PU03_ixKM_Frq_StatusOn_Rattling.qxSignal OR _0303_05_PU04_ixKM_Frq_StatusOn_Rattling.qxSignal OR _0303_05_PU05_ixKM_Frq_StatusOn_Rattling.qxSignal;
VFD001_ixStopped(CLK:= xPuKmFrqWorked, Q=> );
