// Условия включения контактора ЧРП основной и резерв IF ix1KM8_qxOn THEN _0303_05_1KM8_qxOn := TRUE; ELSIF ix1KM8_qxOff THEN _0303_05_1KM8_qxOn := FALSE; END_IF;
IF ix2KM8_qxOn THEN _0303_05_2KM8_qxOn := TRUE; ELSIF ix2KM8_qxOff THEN _0303_05_2KM8_qxOn := FALSE; END_IF;
