// Насос PU2 включение через ЧРП и условия переключения на байпас RS_0303_05_PU02_qxKM_Frq_cmdOn( SET:= (VFD001.qxStart AND NOT VFD001.ixDeviceReserveOn) OR (VFD002.qxStart AND VFD001.ixDeviceReserveOn) AND iIPU_Select = 2, RESET1:= (VFD001.qxStop OR (varVFD001.ixEmergencyStop OR varVFD002.ixEmergencyStop) OR ixKM_PU01) AND iIPU_Select = 2, Q1=>_0303_05_PU02_qxKM_Frq_cmdOn );
