ST.xhtml:
// Вход: SetFrequencyHz : REAL (0.0 .. 50.0)
// Выход: ScaledValue : UINT (0 .. 1000)

// Масштабирование: (SetFrequencyHz / 50.0) * 1000
TempValue := (irSetFrequencyHz / irSetFrequencyHzMax) * 1000.0;

// Обрезка границ
IF TempValue < 0.0 THEN
    qIScaledValue := 0;
ELSIF TempValue > 1000.0 THEN
    qIScaledValue := 1000;
ELSE
	qIScaledValue := REAL_TO_UINT(TempValue);
END_IF;

