	qxFrqSmoothStop := Auto_ixFrqSmoothStop;

	qrSetFreq := Auto_iqrSetFreq;

	qxKM_PU01 := Auto_ixKM_PU01;
	qxKM_PU02 := Auto_ixKM_PU02;
	qxKM_PU03 := Auto_ixKM_PU03;
	qxKM_PU04 := Auto_ixKM_PU04;
	qxKM_PU05 := Auto_ixKM_PU05;
