ST.xhtml:
rtrig_ixStart(CLK:= ixStart, Q=> );
rtrig_ixFastStart(CLK:= ixFastStart, Q=> );
rtrig_ixStop(CLK:= ixStop, Q=> );
// ftrig_StopWorkProcess(CLK:= qrFreq > 0, Q=> );
rtrig_ixEmergencyStop(CLK:= ixEmergencyStop, Q=> );
rtrig_ixReset(CLK:= ixReset, Q=> );
rtrig_ixStarted(CLK:= ixStarted, Q=> );
rtrig_ixStopped(CLK:= ixStopped, Q=> );

ProcessErrorHandling();
ProcessFrequencyControl();
// ProcessSignal();
ProcessTimers();

qxEmergencyStopStatus := FALSE;
qxErrorInvalidInaccuracy := FALSE;
qxErrorDevice := FALSE;
qxDelayError := FALSE;
xErrorFixation := FALSE;

IF xStart THEN
	qxStart := TRUE;
END_IF;

IF NOT xStart THEN
	qxStart := FALSE;
END_IF;

IF xStart THEN
	cmdStart(TRUE);
	qrFreq := iqrSetFreq;
END_IF;

IF xStop THEN
	qxStop := TRUE;
END_IF;

IF NOT xStop THEN
	qxStop := FALSE;
END_IF;

IF xStop THEN
	cmdStop(TRUE);
	qxEmergencyStopStatus := TRUE;
END_IF;

qxStartStatus := qxStart;
qxStopStatus := qxStop;

// Обновление статуса аварийной остановки по сигналу кнопки
IF rtrig_ixEmergencyStop.Q THEN qxEmergencyStopStatus := TRUE; END_IF;

// Остановка рабочего процесса по сигналу заднего фронта qrFreq > 0
// IF ftrig_StopWorkProcess.Q THEN xWorkProcess := FALSE; END_IF;

// Остановка рабочего процесса xWorkProcess AND qrFreq = 0 по таймеру
IF ton_stopWorkProcess.Q THEN xWorkProcess := FALSE; END_IF;

IF iqrSetFreq + irUpdateCycleStep < qrFreq THEN
	CalculateNewFrequency := qrFreq - irUpdateCycleStep;
ELSIF iqrSetFreq - irUpdateCycleStep > qrFreq THEN
	CalculateNewFrequency := qrFreq + irUpdateCycleStep;
ELSE
	CalculateNewFrequency := iqrSetFreq;
END_IF;

IF irActualFreq < qrFreq THEN
	CheckFrequencyInaccuracy := qrFreq - irActualFreq <= irActualFreqInaccuracy;
ELSIF irActualFreq > qrFreq THEN
	CheckFrequencyInaccuracy :=irActualFreq - qrFreq <= irActualFreqInaccuracy;
ELSIF irActualFreq = qrFreq THEN
	CheckFrequencyInaccuracy := TRUE;
ELSE
	CheckFrequencyInaccuracy := FALSE;
END_IF;

ShouldUpdateFrequency := iqrSetFreq <> qrFreq 
	AND CheckFrequencyInaccuracy()
	AND rtrig_updateCycle.Q
	// AND NOT xErrorFixation
	// AND ixDeviceReady
	AND xReadyToCalculate
	// AND xWorkProcess
	// AND NOT qxEmergencyStopStatus
	// AND (iqrSetFreq <= irMaxFreq) 
	// AND (iqrSetFreq >= 0);
	;

// Таймер для цикла обновления
blink_updateCycle(
    ENABLE:= xReadyToCalculate, 
    TIMELOW:= itUpdateCycle, 
    TIMEHIGH:= T#100MS
);
rtrig_updateCycle(CLK:= blink_updateCycle.OUT, Q=> );
(*
// Таймер остановки рабочего процесса
ton_stopWorkProcess(
    IN:= xWorkProcess AND qrFreq = 0, 
    PT:= T#5S
);

ton_StopDisable(
	IN:= qxStop, 
	PT:= itStartStopDelay, 
	Q=> , 
	ET=> );

ton_StartDisable(
	IN:= qxStart, 
	PT:= itStartStopDelay,
	Q=> , 
	ET=> );
	*)

// Обобщённая фиксация всех ошибок в одну переменную
IF GetErrorDevice() OR GetErrorInvalidInaccuracy() OR rtrig_ixEmergencyStop.Q THEN
	xErrorFixation := TRUE;
END_IF;

IF GetErrorDevice() THEN qxErrorDevice := TRUE; END_IF;
IF GetErrorInvalidInaccuracy() THEN qxErrorInvalidInaccuracy := TRUE; END_IF;
IF GetDelayError() THEN qxDelayError := TRUE; END_IF;

// Работа старта
IF rtrig_ixStart.Q THEN xReadyToCalculate := TRUE; END_IF;

// Работа быстрого старта
IF rtrig_ixFastStart.Q THEN 
	xReadyToCalculate := TRUE;
	qrFreq := iqrSetFreq;
END_IF

// Плавное изменение частоты
IF ShouldUpdateFrequency() THEN
    qrFreq := CalculateNewFrequency();
END_IF;

// Защита от задания частоты вне диапазона
IF iqrSetFreq > irMaxFreq THEN iqrSetFreq := irMaxFreq; END_IF;
IF iqrSetFreq < 0 THEN iqrSetFreq := 0; END_IF;

// Задание частоты на 0 при получении сигнала остановки
IF rtrig_ixStop.Q AND NOT ixSmoothStop THEN 
	qrFreq := 0; 
	xReadyToCalculate := FALSE; 
END_IF;

IF rtrig_ixStop.Q AND ixSmoothStop THEN
	temp_iqrSetFreq := iqrSetFreq;
	xSmoothStopProcess := TRUE;
	iqrSetFreq := 0;
END_IF

UpdateStatus();

// Обработка сигнала старт
IF rtrig_ixStart.Q OR rtrig_ixFastStart.Q THEN 
	cmdStart(TRUE); 
	cmdStop(FALSE); 
	xWorkProcess := TRUE;
END_IF;
// Завершение сигнала старт
IF rtrig_ixStarted.Q OR ton_StartDisable.Q THEN 
	cmdStart(FALSE);
END_IF;

// Отработка сигнала стоп
IF rtrig_ixStop.Q AND ixSmoothStop THEN 
	cmdStop(TRUE); 
	cmdStart(FALSE); 
	xWorkProcess := TRUE;
END_IF;

IF rtrig_ixStop.Q AND NOT ixSmoothStop THEN 
	cmdStop(TRUE); 
	cmdStart(FALSE); 
	xWorkProcess := FALSE;
END_IF;
// Завершение сигнала стоп
IF rtrig_ixStopped.Q OR ton_StopDisable.Q THEN 
	cmdStop(FALSE); 
END_IF;

// Обработка сигнала аварийной остановки
IF rtrig_ixEmergencyStop.Q THEN
	cmdStop(TRUE);
END_IF;

// Обработка сигнала сброса ошибок
IF rtrig_ixReset.Q THEN
	cmdReset();
END_IF;

// Обработка сигнала быстрого старта
IF rtrig_ixFastStart.Q THEN
	cmdFastStart(TRUE);
END_IF;

InterfaceAsPlainText.xhtml:
FUNCTION_BLOCK FB_VFD_CTRL
VAR_INPUT
//	Конфигурация
	irMaxFreq : REAL; 			// Максимальное задание частоты
	itUpdateCycle : TIME; 		// Время обновления цикла плавного пуска/остановки в TIME 
	irUpdateCycleStep : REAL; 	// Шаг обновления частоты за 1 цикл
	irActualFreqInaccuracy : REAL;	// Максимальная допустимая погрешность для ЧРП
	ixSmoothStop : BOOL;		// Режим плавной остановки
	itStartStopDelay : TIME;	// Время задержки сигнала старт и стоп до его отключения и выхода ошибки долгого ожидания отклика механизма

//	Команды
	ixStart : BOOL;				// Запуск
	ixFastStart : BOOL;			// Запуск без плавного задания частоты
	ixStop : BOOL;				// Остановка
	ixEmergencyStop : BOOL;		// Аварийная остановка (сброс с выходным статусом)
	ixReset : BOOL;				// Сброс ошибок ФБ

//	Получение сигналов
	ixStarted : BOOL;			// Получение сигнала о старте механизма
	ixStopped : BOOL;			// Получение сигнала о стопе механизма
	ixDeviceError : BOOL;		// Ошибка исполнительного механизма
	ixDeviceReady : BOOL;		// Готовность исполнительного механизма
	irActualFreq : REAL;		// Показания текущей частоты устройства
END_VAR
VAR_OUTPUT
// 	Основное
	qrFreq : REAL;				// Задание частоты на выход
	qxStart : BOOL;				// Запуск исполнительного механизма
	qxStop : BOOL;				// Остановка исполнительного механизма

// Обратная связь
	qxStartStatus : BOOL;						// Статус запуска
	qxStopStatus : BOOL;						// Статус остановки
	qxEmergencyStopStatus : BOOL;				// Статус аварийной остановки
	qxErrorInvalidInaccuracy : BOOL;			// Ошибка большой погрешности
	qxErrorDevice : BOOL;						// Ошибка исполнительного механизма (транзит через блок)
	qxDelayError : BOOL;						// Ошибка долгого ожидания обратной связи механизма после выполнения
END_VAR
VAR_IN_OUT
// Основное
	iqrSetFreq : REAL;			// Задание частоты на вход
END_VAR
VAR
	temp_iqrSetFreq : REAL;
	xSmoothStopProcess : BOOL;
	blink_updateCycle : BLINK;
	xWorkProcess : BOOL;
	xReadyToCalculate : BOOL;
	xPossibleInac : BOOL;
	xErrorFixation : BOOL;
	ton_stopWorkProcess : TON;
	ton_StopDisable : TON;
	ton_StartDisable : TON;


	ftrig_xSmoothStopProcess : F_TRIG;
	rtrig_updateCycle : R_TRIG;
	rtrig_ixStart : R_TRIG;
	rtrig_ixFastStart : R_TRIG;
	rtrig_ixStop : R_TRIG;
	rtrig_ixEmergencyStop : R_TRIG;
	rtrig_ixReset : R_TRIG;
	rtrig_ixStarted : R_TRIG;
	rtrig_ixStopped : R_TRIG;
	ftrig_StopWorkProcess : F_TRIG;
END_VAR

