ST.xhtml:
// Старт насоса от ЧРП
IF xStart AND Condition_PU_VFD(vfd_iChange, pu_iChange) THEN
	Set_KM8(vfd_iChange, TRUE);
END_IF;
Set_VFD_Start(vfd_iChange, xStart AND Condition_PU_VFD(vfd_iChange, pu_iChange));

// Работа смены насоса с БП на ЧРП
Set_VFD_FastStart(vfd_iChange, xChangePUToVFD AND Condition_PU_ChangeToVFD(pu_iChange, vfd_iChange));

// Аварийная остановка ЧРП
Set_VFD_EmergencyStop(vfd_iChange, xEmergencyStop);

// Работа остановки ЧРП и переключения на БП
Set_VFD_Stop(vfd_iChange, 
	(xStop AND GetStatus_PU_VFD(pu_iChange))
	OR (xChangePUToBP AND Condition_PU_ChangeToBP(pu_iChange, vfd_iChange)));

// Работа обобщённой аварийной остановки
Set_VFD_EmergencyStop(vfd_iChange, xEmergencyStopCommon);


// Работа сброса ошибок ФБ ЧРП
IF xReset THEN
	Set_VFD_Reset(vfd_iChange, TRUE);
END_IF;

