ST.xhtml:
// IF xReset THEN
// 	
// END_IF;


// Запуск по команде на БП
IF xStartBP AND Condition_PU_BP(pu_iChange) AND NOT GetStatus_PU_Manual(pu_iChange) THEN
	Set_PU_BP(pu_iChange, TRUE);
END_IF;

IF xStartVFD AND Condition_PU_VFD(vfd_iChange, pu_iChange) THEN
	Set_PU_VFD(pu_iChange, TRUE);
END_IF;

IF xStop THEN
	Set_PU_BP(pu_iChange, FALSE);
	Set_PU_VFD(pu_iChange, FALSE);
END_IF;

IF xEStopCommmon THEN
	Set_PU_BP(1, FALSE);
	Set_PU_VFD (1, FALSE);
	Set_PU_BP(2, FALSE);
	Set_PU_VFD (2, FALSE);
	Set_PU_BP(3, FALSE);
	Set_PU_VFD (3, FALSE);
	Set_PU_BP(4, FALSE);
	Set_PU_VFD (4, FALSE);
	Set_PU_BP(5, FALSE);
	Set_PU_VFD (5, FALSE);
	Set_PU_EmergencyStopCommon(TRUE);
END_IF;

// Работа смены ЧРП и БП
IF xChange_toBP AND Condition_PU_ChangeToBP(pu_iChange, vfd_iChange) THEN
	Set_PU_VFD(pu_iChange, FALSE);	
	Set_PU_BP(pu_iChange, TRUE);
END_IF;
// Работа смены БП на ЧРП
IF xChange_toVFD AND Condition_PU_ChangeToVFD(pu_iChange, vfd_iChange) THEN
	Set_PU_BP(pu_iChange, FALSE);
	Set_PU_VFD(pu_iChange, TRUE);
END_IF;

