	xChange_toBP : BOOL;
END_VAR
VAR
END_VAR

ST.xhtml:
pu_ivalue_BPWorked := GetValue_PU_CountWorkStatusBP();
pu_ivalue_VFDWorked := GetValue_PU_CountWorkStatusVFD();

IF pu_ivalue_VFDWorked > 1 THEN
	Set_PU_VFD(1, FALSE);
	Set_PU_VFD(2, FALSE);
	Set_PU_VFD(3, FALSE);
	Set_PU_VFD(4, FALSE);
	Set_PU_VFD(5, FALSE);
	Set_PU_EmergencyStopCommon(TRUE);
END_IF;

Process_PU_Change();

Process_PU_Signals();

Process_PU_Commands(
	xStartBP:= rtrig_puCmd_xStartBP.Q, 
	xStartVFD:= rtrig_puCmd_xStartVFD.Q, 
	xStop:= rtrig_puCmd_xStop.Q,
	xEStopCommmon := rtrig_puCmd_xEmergencyStopCommon.Q, 
	xReset:= rtrig_puCmd_xReset.Q, 
	xChange_toVFD:= rtrig_puCmd_xChange_VFD.Q, 
	xChange_toBP:= rtrig_puCmd_xChange_toBP.Q);

// Переключение контактора в соответствии с работающим частотником
Set_KM8(vfd_iChange, GetStatus_PU_VFD_Common());

// Отключение контакторов, не участвующих в работе действующего ЧРП
CASE vfd_iChange OF
	1: Set_KM8(2, FALSE);
	2: Set_KM8(1, FALSE);
END_CASE;
// Отключение контакторов КМ8 при условии их одновременной работы
IF GetStatus_KM8(1) AND GetStatus_KM8(2) THEN Set_KM8(1, FALSE); Set_KM8(2, FALSE); END_IF;
// Аварийная остановка ЧРП при обнаружении их одновременной работы
IF GetStatus_VFD_Started(1) AND GetStatus_VFD_Started(2) THEN Set_VFD_EmergencyStop(1, TRUE); Set_VFD_EmergencyStop(2, TRUE); END_IF;

rtrig_puCmd_xStartBP(CLK:= xPU_StartBP.qxSignal, Q=> );
rtrig_puCmd_xStartVFD(CLK:= xPU_StartVFD.qxSignal, Q=> );
rtrig_puCmd_xStop(CLK:= xPU_Stop.qxSignal, Q=> );
rtrig_puCmd_xReset(CLK:= xPU_Reset.qxSignal, Q=> );
rtrig_puCmd_xChange_toBP(CLK:= xPU_Change_VFDtoBP.qxSignal, Q=> );
rtrig_puCmd_xChange_VFD(CLK:= xPU_Change_BPtoVFD.qxSignal, Q=> );
rtrig_puCmd_xEmergencyStopCommon(CLK:= xPU_xEmergencyStopCommon.qxSignal OR pu_xEmergencyStopCommonStatus);

rtrig_vfdCmd_xStart(CLK:= xVFD_Start.qxSignal, Q=> );
// rtrig_vfdCmd_xEmergencyStop(CLK:= xVFD_Start.qxSignal, Q=> );
rtrig_vfdCmd_xStop(CLK:= xVFD_Stop.qxSignal, Q=> );
rtrig_vfdCmd_xReset(CLK:= xVFD_Reset.qxSignal, Q=> );
rtrig_vfdCmd_xChangeToMain(CLK := xVFD_ChangeToMain.qxSignal);
rtrig_vfdCmd_xChangeToReserve(CLK := xVFD_ChangeToReserve.qxSignal);
rtrig_vfdCmd_xEmergencyStopCommon(CLK:= xVFD_EmergencyStopCommon.qxSignal OR pu_xEmergencyStopCommonStatus);

rtrig_byCmd_xOpen(CLK:= xBY_Open.qxSignal, Q=> );
