METHOD Process_VFD_Change
VAR_INPUT
	xChangeToMain : BOOL;
	xChangeToReserve : BOOL;
END_VAR
VAR
	xChangeToMainProcess : BOOL;
	xChangeToReserveProcess : BOOL;
END_VAR

METHOD Process_VFD_Change
VAR_INPUT
	xChangeToMain : BOOL;
	xChangeToReserve : BOOL;
END_VAR
VAR
	xChangeToMainProcess : BOOL;
	xChangeToReserveProcess : BOOL;
END_VAR

ST.xhtml:
IF xChangeToMain AND Condition_VFD_ChangeToMain() THEN xChangeToMainProcess := TRUE; END_IF;
IF xChangeToReserve AND Condition_VFD_ChangeToReserve() THEN xChangeToReserveProcess := TRUE; END_IF;

// Процесс переключения ЧРП на ОСНОВНОЙ
IF xChangeToMainProcess THEN
	Set_VFD_Stop(2, TRUE);
	IF NOT GetStatus_VFD_Started(2) AND NOT GetStatus_PU_VFD(pu_iChange) THEN
		Set_VFD_Change(1);
	ELSIF NOT GetStatus_VFD_Started(2) AND GetStatus_PU_VFD(pu_iChange) THEN
		Set_VFD_Change(1);
		Set_VFD_FastStart(1, TRUE);
	END_IF;
END_IF;

// Процесс переключения ЧРП на РЕЗЕРВ
IF xChangeToReserveProcess THEN
	Set_VFD_Stop(1, TRUE);
	IF NOT GetStatus_VFD_Started(1) AND NOT GetStatus_PU_VFD(pu_iChange) THEN
		Set_VFD_Change(2);
	ELSIF NOT GetStatus_VFD_Started(1) AND GetStatus_PU_VFD(pu_iChange) THEN
		Set_VFD_Change(2);
		Set_VFD_FastStart(2, TRUE);
	END_IF;
END_IF;

