IF ftrig_PU1_cmdStop_ProcessLongTime.Q THEN xStopProcessPU1 := FALSE; END_IF;
ton_PU1_CommandStop_ProcessLongTime(IN:= xStopProcessPU1, PT:= itCommandProcessLongTime, Q=> errPU1.xError_LongTime_Cmd_Stop, ET=> );

// PU2
ftrig_PU2_cmdStop_ProcessLongTime(CLK:= GetStatus_PU_BP(2) OR GetStatus_PU_VFD(2));
IF ftrig_PU2_cmdStop_ProcessLongTime.Q THEN xStopProcessPU2 := FALSE; END_IF;
