ST.xhtml:
ftrig_qxOpenProcessStatus(CLK:= qxOpen, Q=> );
ftrig_qxCloseProcessStatus(CLK:= qxClose, Q=> );

IF ftrig_qxOpenProcessStatus.Q THEN qxOpenProcessStatus := FALSE; END_IF;
IF ftrig_qxCloseProcessStatus.Q THEN qxCloseProcessStatus := FALSE; END_IF;

qxOpenStatus := GetStatusOpen();
qxClosedStatus := GetStatusClosed();

qxManualStatus := GetStatusManual();

qxErrorCommonStatus := GetStatusCommonError();

InterfaceAsPlainText.xhtml:
METHOD CloseOpenProcess : BOOL
VAR_INPUT
END_VAR
VAR
	xOpen : BOOL;
	xClose : BOOL;

	rtrig_ixOpenCmd : R_TRIG;
	rtrig_ixCloseCmd : R_TRIG;
	rtrig_ixStopCmd : R_TRIG;
	rtrig_ixResetCmd : R_TRIG;

	rtrig_ixOpenStatus : R_TRIG;
	rtrig_ixClosedStatus : R_TRIG;

END_VAR

ST.xhtml:
rtrig_ixOpenCmd(CLK:= ixOpenCmd, Q=> );
rtrig_ixCloseCmd(CLK:= ixCloseCmd, Q=> );
rtrig_ixStopCmd(CLK:= ixStopCmd, Q=> );
rtrig_ixResetCmd(CLK:= ixResetCmd, Q=> );

rtrig_ixOpenStatus(CLK:= ixOpenStatus, Q=> );
rtrig_ixClosedStatus(CLK:= ixClosedStatus, Q=> );

IF NOT GetStatusManual() THEN
	IF rtrig_ixOpenCmd.Q AND GetStatusReady() AND NOT GetStatusOpen() THEN
		cmdOpen(TRUE);
	END_IF;

	IF rtrig_ixCloseCmd.Q AND GetStatusReady() AND NOT GetStatusClosed() THEN
		cmdClose(TRUE);
	END_IF;

	IF OpenCloseProcessStatus() AND GetStatusOpen() THEN
		cmdOpen(FALSE);
	END_IF;

	IF OpenCloseProcessStatus() AND GetStatusClosed() THEN
		cmdClose(FALSE);
	END_IF;

	IF rtrig_ixStopCmd.Q THEN
		cmdStop(TRUE);
	END_IF;

	IF rtrig_ixResetCmd.Q THEN cmdReset(); END_IF;

END_IF;

