	xOpen : BOOL;
	xClose : BOOL;

	rtrig_ixOpenCmd : R_TRIG;
	rtrig_ixCloseCmd : R_TRIG;
	rtrig_ixStopCmd : R_TRIG;
	rtrig_ixResetCmd : R_TRIG;

	rtrig_ixOpenStatus : R_TRIG;
	rtrig_ixClosedStatus : R_TRIG;

END_VAR

ST.xhtml:
IF xClose THEN
	qxClose := TRUE;
	qxCloseProcessStatus := TRUE;
	qxStoppedStatus := FALSE;
ELSIF NOT xClose THEN
	qxClose := FALSE;
	qxCloseProcessStatus := FALSE;
END_IF;

